mapper.select:
with ins as (INSERT INTO apps_layers (app_id, layer_id, is_default, display_rule, style_rule) VALUES
#{appId}, #{item.layerId}, #{item.isDefault}, #{item.displayRule}::jsonb, #{item.styleRule}::jsonb
ON CONFLICT (app_id, layer_id) DO UPDATE SET (is_default, display_rule, style_rule) = (EXCLUDED.is_default, EXCLUDED.display_rule, EXCLUDED.style_rule) RETURNING *) select app_id as appId, layer_id as layerId, is_default as isDefault, display_rule as displayRule, style_rule as styleRule from ins;
