mapper.select:
with
g as (select ST_GeomFromGeoJSON(#{geometry}) as inpgeom),
lrsids as (select l.id from layers l where is_visible
#{id}
and (st_intersects(l.geom, (select inpgeom from g)) or is_global)
and is_global
and l.public_id in (select layer_id from apps_layers where app_id = #{appId})
and owner = #{userName}
and is_public and owner != #{userName}
and (is_public or owner = #{userName})
and is_public
union select l.id from layers l join lateral ( select layer_id from layers_features as lf where st_intersects(lf.geom, (select inpgeom from g)) and l.id = lf.layer_id limit 1) as lff on true where l.is_visible and l.geom is null and not l.is_global
#{id}
and l.public_id in (select layer_id from apps_layers where app_id = #{appId})
and owner = #{userName}
and is_public and owner != #{userName}
and (is_public or owner = #{userName})
and is_public
) select count(distinct l.id) AS numberMatched from lrsids l

with
g as (select ST_GeomFromGeoJSON(#{geometry}) as inpgeom),
lrsids as (select l.id from layers l where is_visible
#{id}
and (st_intersects(l.geom, (select inpgeom from g)) or is_global)
and is_global
and l.public_id in (select layer_id from apps_layers where app_id = #{appId})
and owner = #{userName}
and is_public and owner != #{userName}
and (is_public or owner = #{userName})
and is_public
union select l.id from layers l join lateral ( select layer_id from layers_features as lf where st_intersects(lf.geom, (select inpgeom from g)) and l.id = lf.layer_id limit 1) as lff on true where l.is_visible and l.geom is null and not l.is_global
#{id}
and l.public_id in (select layer_id from apps_layers where app_id = #{appId})
and owner = #{userName}
and is_public and owner != #{userName}
and (is_public or owner = #{userName})
and is_public
) select lrs.id, lrs.public_id as publicid, lrs.name as name, lrs.description, lrs.copyrights, lrs.properties, lrs.group_id, lrs.category_id, lrs.type, lrs.url, lrs.api_key as apiKey, lrs.last_updated as lastupdated, lrs.source_updated as sourcelastupdated, lrs.is_public as isPublic, lrs.is_visible as isVisible, lrs.feature_properties as featureProperties, lrs.owner as owner, lrs.tile_size as tileSize, lrs.min_zoom as minZoom, lrs.max_zoom as maxZoom,
al.display_rule as displayRule, coalesce(nullif(al.legend_style_config, '{}'::jsonb), nullif(lrs.legend_style_config, '{}'::jsonb)) as legendStyle, coalesce(nullif(al.map_style_config, '{}'::jsonb), nullif(lrs.map_style_config, '{}'::jsonb)) as mapStyle, coalesce(nullif(al.popup_config, '{}'::jsonb), nullif(lrs.popup_config, '{}'::jsonb)) as popupConfig,
lrs.legend_style_config as legendStyle, lrs.map_style_config as mapStyle, lrs.popup_config as popupConfig,
json_strip_nulls(json_build_object( 'name', gp."name", 'isOpened', gp.is_opened, 'mutuallyExclusive', gp.mutually_exclusive, 'order', gp."order")) as group, json_strip_nulls(json_build_object( 'name', cp."name", 'isOpened', cp.is_opened, 'mutuallyExclusive', cp.mutually_exclusive, 'order', cp."order")) as category from layers as lrs
left join apps_layers al on al.app_id = #{appId} and al.layer_id = lrs.public_id
left join layers_group_properties gp on gp.id = lrs.group_id left join layers_category_properties cp on cp.id = lrs.category_id where lrs.id in (select id from lrsids) order by lrs.id limit #{limit} offset #{offset}

select l.id, l.public_id as publicId, l.name, l.description, l.url, l.type, l.geom as geometry, l.last_updated as lastUpdated, l.source_updated as sourceLastUpdated, l.copyrights, l.properties, l.is_public as isPublic, l.is_visible as isVisible, l.feature_properties as featureProperties, l.owner, l.tile_size as tileSize, l.min_zoom as minZoom, l.max_zoom as maxZoom, json_strip_nulls(json_build_object( 'name', gp."name", 'isOpened', gp.is_opened, 'mutuallyExclusive', gp.mutually_exclusive, 'order', gp."order")) as group, json_strip_nulls(json_build_object( 'name', cp."name", 'isOpened', cp.is_opened, 'mutuallyExclusive', cp.mutually_exclusive, 'order', cp."order")) as category from layers l left join layers_group_properties gp on gp.id = l.group_id left join layers_category_properties cp on cp.id = l.category_id where is_visible and public_id in (select layer_id from apps_layers where app_id = #{appId}
and is_default
);
