mapper.select:
select count(*) AS numberMatched from layers l where is_visible
#{id}
and ((l.geom isnull and l.id in (select layer_id from layers_features ls where ST_Intersects(ls.geom, ST_GeomFromGeoJSON(#{geometry})))) or ST_Intersects(l.geom, ST_GeomFromGeoJSON(#{geometry})))
and l.public_id in (select layer_id from apps_layers where app_id = #{appId})
and owner = #{userName}
and is_public and owner != #{userName}
and (is_public or owner = #{userName})
and is_public
;

with lrs as (select l.id, l.public_id as publicid, l.name as name, l.description, l.copyrights, l.properties, l.group_id, l.category_id, l.type, l.url, l.last_updated as lastupdated, l.source_updated as sourcelastupdated, l.is_public as isPublic, l.is_visible as isVisible, l.feature_properties as featureProperties, l.owner as owner from layers l where is_visible
#{id}
and ((l.geom isnull and l.id in (select layer_id from layers_features ls where ST_Intersects(ls.geom, ST_GeomFromGeoJSON(#{geometry})))) or ST_Intersects(l.geom, ST_GeomFromGeoJSON(#{geometry})))
and l.public_id in (select layer_id from apps_layers where app_id = #{appId})
and owner = #{userName}
and is_public and owner != #{userName}
and (is_public or owner = #{userName})
and is_public
group by l.id limit #{limit} offset #{offset} ) select lrs.*, al.style_rule as styleRule, al.display_rule as displayRule, json_strip_nulls(json_build_object( 'name', gp."name", 'isOpened', gp.is_opened, 'mutuallyExclusive', gp.mutually_exclusive, 'order', gp."order")) as group, json_strip_nulls(json_build_object( 'name', cp."name", 'isOpened', cp.is_opened, 'mutuallyExclusive', cp.mutually_exclusive, 'order', cp."order")) as category from lrs left join apps_layers al on al.app_id = #{appId} and al.layer_id = lrs.publicid left join layers_group_properties gp on gp.id = lrs.group_id left join layers_category_properties cp on cp.id = lrs.category_id order by lrs.id

select id, public_id as publicId, name, description, url, type, geom as geometry, last_updated as lastUpdated, source_updated as sourceLastUpdated, copyrights, properties, is_public as isPublic, is_visible as isVisible, feature_properties as featureProperties, owner from layers where is_visible and public_id in (select layer_id from apps_layers where app_id = #{appId}
and is_default
);
