mapper.select:
with lrs as (select l.id, l.public_id as publicid, l.name as name, l.description, l.last_updated as lastupdated, l.source_updated as sourcelastupdated, count(*) OVER() AS numberMatched from layers l
#{id}
group by l.id limit #{limit} offset #{offset}), ext as (select f.layer_id, st_3dextent(f.geom) as spat, (select f2.last_updated from layers_features f2 where f2.last_updated is not null and f2.layer_id = f.layer_id order by f2.last_updated asc nulls first limit 1) as lowerdt, (select f2.last_updated from layers_features f2 where f2.last_updated is not null and f2.layer_id = f.layer_id order by f2.last_updated desc nulls first limit 1) as upperdt from layers_features f join lrs on lrs.id = f.layer_id group by f.layer_id) select lrs.*, case when ext.spat is not null then format('[%s,%s,%s,%s,%s,%s]', st_xmin(ext.spat), st_ymin(ext.spat), st_zmin(ext.spat), st_xmax(ext.spat), st_ymax(ext.spat), st_zmax(ext.spat)) end as spatialextent, case when (lowerdt is not null or upperdt is not null ) then tstzrange(lowerdt, upperdt, format('%s%s', '[', ']' )) end as temporalextent from lrs left join ext on lrs.id = ext.layer_id
