mapper.select:
select count(*) AS numberMatched from layers l where is_visible
#{id}
and ((l.geom isnull and l.id in (select layer_id from layers_features ls where ST_Intersects(ls.geom, ST_GeomFromGeoJSON(#{geometry})))) or ST_Intersects(l.geom, ST_GeomFromGeoJSON(#{geometry})))
and (is_public or owner = #{userName})
and is_public
;
